var myApp=angular.module("myApp",[]);myApp.controller("MainController",["$scope","$http",function(a,b){a.task={},a.taskList={},a.getTasks=function(){b.get("/todos").success(function(b){a.taskList=b,console.log(b)}).error(function(a){console.log("Error: "+a)})},a.getTasks(),a.deleteTask=function(c){b["delete"]("/todos/"+c).success(function(b){a.taskList=b,console.log(b)}).error(function(a){console.log("Error: "+a)})},a.createTask=function(){b.post("/todos",a.task).success(function(b){console.log(a.task),a.task={},a.taskList=b,console.log(b)}).error(function(a){console.log("Error: "+a)}),a.getTasks()},a.completeTask=function(c,d,e){b.put("/todos/"+c,{text:d,complete:!e}).success(function(b){a.task={},a.taskList=b,console.log(b)}).error(function(a){console.log("Error: "+a)})}}]);